      out.write("\n");
      out.write("var fn_dupCheck = function (tag) {\n");
      out.write("	\n");
      out.write("	if($(tag).val() != \"\") {\n");
      out.write("		$.ajax({\n");
      out.write("	        url	:'/member/dupCheck.json',\n");
      out.write("	        type:'POST',\n");
      out.write("	        data:{memberId:$(tag).val()},\n");
      out.write("	        success: function(res) {\n");
      out.write("				if(res > 0) {\n");
      out.write("					return alert(\"아이디가 중복됩니다.\");\n");
      out.write("				}\n");
      out.write("\n");
      out.write("	        }\n");
      out.write("	    });		\n");
      out.write("	}\n");
      out.write("	\n");
      out.write("}\n");
      out.write("\n");
      out.write("var fn_hide = function () {\n");
      out.write("	$('.pop.on').removeClass('on');\n");
      out.write("}\n");
      out.write("\n");
      out.write("var fn_addRow = function () {\n");
      out.write("\n");
      out.write("    if($('#sMemberId').length > 0) {\n");
      out.write("        return alert('아직 작업중인 row 가 있습니다.\\n작업 완료 후 추가하세요.');\n");
      out.write("    }\n");
      out.write("\n");
      out.write("	var addTr = '<tr>';\n");
      out.write("    addTr += '<td></td>';\n");
      out.write("    addTr += '<td><input id=\"sMemberId\" onfocusout=\"fn_dupCheck(this)\" /></td>';\n");
      out.write("    addTr += '<td><input id=\"sMemberNm\" /></td>';\n");
      out.write("    addTr += '<td>';\n");
      out.write("    addTr += '  <input type=\"radio\" name=\"sSex\" id=\"sMale\" value=\"male\" checked/><label for=\"sMale\">남</label>';\n");
      out.write("    addTr += '  <input type=\"radio\" name=\"sSex\" id=\"sFemale\" value=\"female\"/><label for=\"sFemale\">여</label>';\n");
      out.write("    addTr += '</td>';\n");
      out.write("    addTr += '<td>';\n");
      out.write("    addTr += '  <select name=\"country\" id=\"sCountry\" onchange=\"Change(this,`sCity`)\">';\n");
      out.write("    addTr += '      <option value=\"none\" selected>국가</option>';\n");
      out.write("    addTr += '      <option value=\"한국\">한국</option>';\n");
      out.write("    addTr += '      <option value=\"미국\">미국</option>';\n");
      out.write("    addTr += '      <option value=\"중국\">중국</option>';\n");
