      out.write("    addTr += '<td>';\n");
      out.write("    addTr += '  <select id=\"sCity\">';\n");
      out.write("    addTr += '      <option value=\"none\" selected>도시</option>';\n");
      out.write("    addTr += '  </select>';\n");
      out.write("    addTr += '</td>';\n");
      out.write("    addTr += '</tr>';\n");
      out.write("    $('#data_tbody').prepend(addTr);\n");
      out.write("\n");
      out.write("}\n");
      out.write("\n");
      out.write("// 초기화\n");
      out.write("var fn_reset = function () {\n");
      out.write("\n");
      out.write("    $('#memberId , #memberNm , #modifyId').val('');\n");
      out.write("\n");
      out.write("    // 날짜 오늘날짜로 셋팅\n");
      out.write("    var date = new Date();\n");
      out.write("    var month = date.getMonth().toString().length===1 ? '0'+(date.getMonth()+1) : date.getMonth();\n");
      out.write("    $('input[type=date]').val(date.getFullYear() +'-'+ month + '-' + date.getDate());\n");
      out.write("\n");
      out.write("    $('#sexM').prop('checked',true);\n");
      out.write("\n");
      out.write("    $('#country').val('none');\n");
      out.write("	$('#city').empty().append('<option value=\"none\" selected>도시</option>');\n");
      out.write("}\n");
      out.write("\n");
      out.write("// 목록\n");
      out.write("var fn_list = function () {\n");
      out.write("    var param = {};\n");
      out.write("    param.memberId  = $('#memberId').val();\n");
      out.write("    param.memberNm  = $('#memberNm').val();\n");
      out.write("    param.startDate = $('#startDate').val().replaceAll('-','');\n");
      out.write("    param.endDate   = $('#endDate').val().replaceAll('-','');\n");
      out.write("    param.sex       = $('input[name=sex]:checked').val();\n");
      out.write("    param.country   = $('#country').val();\n");
      out.write("    param.city      = $('#city').val();\n");
      out.write("\n");
      out.write("    $.ajax({\n");
      out.write("        url	:'/member/list.json',\n");
      out.write("        type:'POST',\n");
      out.write("        data:param,\n");
      out.write("        success: function(res) {\n");
      out.write("\n");
      out.write("            $('#data_tbody').empty();\n");
      out.write("\n");
      out.write("            for(var data of res) {\n");
      out.write("                var html = '<tr>';\n");
      out.write("                html += '<td><input data-id=\"'+data.memberId+'\" type=\"checkbox\"></td>';\n");
      out.write("                html += '<td ondblclick=\"fn_detail(`'+data.memberId+'`)\">'+data.memberId+'</td>';\n");
      out.write("                html += '<td>'+data.memberNm+'</td>';\n");
      out.write("                html += '<td>'+data.sex+'</td>';\n");
      out.write("                html += '<td>'+data.country+'</td>';\n");
      out.write("                html += '<td>'+data.city+'</td>';\n");
      out.write("                html += '</tr>';\n");
      out.write("                $('#data_tbody').append(html);\n");
      out.write("            }\n");
      out.write("\n");
      out.write("        }\n");
